import Head from "next/head";
import Link from "next/link";
import { Layout, Menu, Breadcrumb } from "antd";
import {
  DesktopOutlined,
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  LaptopOutlined,
  DatabaseOutlined,
  LoadingOutlined,
} from "@ant-design/icons";

const { Header, Content, Footer, Sider } = Layout;

const { SubMenu } = Menu;

const siteTitle = "Starter Site";

// const collapseButton = React.createElement(
//   collapsed ? MenuUnfoldOutlined : MenuFoldOutlined,
//   {
//     className: "trigger",
//     onClick: toggleCollapsed,
//   }
// );

export default function StandardLayout({ title, children }) {
  return (
    <div>
      <Head>
        <title>{title ? title : siteTitle}</title>
      </Head>
      <Layout style={{ overflow: "hidden" }}>
        <Header className="header d-flex align-items-center">
          {/* <div>{collapseButton}</div> */}
        </Header>
        <Layout>
          <Sider
            width={250}
            style={{ height: "100vh" }}
            className="site-layout-background"
            collapsible
            collapsed={false}
          >
            <Menu
              mode="inline"
              defaultSelectedKeys={["1"]}
              defaultOpenKeys={["sub1"]}
              style={{ height: "100%", borderRight: 0 }}
            >
              <SubMenu key="sub1" icon={<DesktopOutlined />} title="Plex">
                <Menu.Item key="1">The Gauntlet</Menu.Item>
                <Menu.Item key="2">Random Movie</Menu.Item>
                <Menu.Item key="3">Drinking Game</Menu.Item>
              </SubMenu>
              <SubMenu key="sub2" icon={<DatabaseOutlined />} title="Minecraft">
                <Menu.Item key="5">option5</Menu.Item>
                <Menu.Item key="6">option6</Menu.Item>
                <Menu.Item key="7">option7</Menu.Item>
                <Menu.Item key="8">option8</Menu.Item>
              </SubMenu>
              <SubMenu key="sub3" icon={<LaptopOutlined />} title="Streamn">
                <Menu.Item key="9">option9</Menu.Item>
                <Menu.Item key="10">option10</Menu.Item>
                <Menu.Item key="11">option11</Menu.Item>
                <Menu.Item key="12">option12</Menu.Item>
              </SubMenu>
            </Menu>
          </Sider>
          <Layout
            className="p-3"
            style={{
              maxHeight: "100vh",
              overflowY: "scroll",
              paddingBottom: "2rem",
            }}
          ></Layout>
        </Layout>
      </Layout>
    </div>
  );
}
